@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 232 33% 12%;
    --foreground: 0 0% 95%;

    --card: 232 30% 16%;
    --card-foreground: 0 0% 95%;

    --popover: 232 30% 16%;
    --popover-foreground: 0 0% 95%;

    --primary: 252 87% 67%;
    --primary-foreground: 0 0% 100%;

    --secondary: 232 25% 22%;
    --secondary-foreground: 0 0% 95%;

    --muted: 232 25% 20%;
    --muted-foreground: 230 10% 55%;

    --accent: 252 87% 67%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 232 20% 25%;
    --input: 232 25% 20%;
    --ring: 252 87% 67%;

    --radius: 0.75rem;

    --sidebar-background: 232 35% 10%;
    --sidebar-foreground: 0 0% 95%;
    --sidebar-primary: 252 87% 67%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 232 25% 18%;
    --sidebar-accent-foreground: 0 0% 95%;
    --sidebar-border: 232 20% 20%;
    --sidebar-ring: 252 87% 67%;

    /* Custom colors */
    --task-card: 232 28% 18%;
    --task-card-hover: 232 28% 22%;
    --detail-panel: 232 30% 14%;
    --priority-high: 0 84% 60%;
    --priority-medium: 45 93% 58%;
    --priority-low: 142 71% 45%;
    --tag-personal: 252 87% 67%;
    --tag-work: 199 89% 48%;
    --tag-shopping: 142 71% 45%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  }
}

@layer utilities {
  .scrollbar-thin {
    scrollbar-width: thin;
    scrollbar-color: hsl(var(--muted)) transparent;
  }

  .scrollbar-thin::-webkit-scrollbar {
    width: 6px;
  }

  .scrollbar-thin::-webkit-scrollbar-track {
    background: transparent;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb {
    background-color: hsl(var(--muted));
    border-radius: 3px;
  }
}

