export interface SubTask {
  id: string;
  title: string;
  completed: boolean;
}

export interface Task {
  id: string;
  title: string;
  time?: string;
  date: string;
  priority: 'high' | 'medium' | 'low';
  category: string;
  completed: boolean;
  subtasks: SubTask[];
  notes?: string;
}

export interface TaskList {
  id: string;
  name: string;
  icon: string;
  color: string;
  count: number;
}

export interface NavItem {
  id: string;
  name: string;
  icon: string;
  count?: number;
}

import { Search, Calendar, StickyNote, Plus, Tag, ChevronDown, Sun, CalendarDays, User, Briefcase, ShoppingCart } from 'lucide-react';
import { cn } from '@/lib/utils';

interface SidebarProps {
  activeNav: string;
  onNavChange: (nav: string) => void;
}

const navItems = [
  { id: 'today', name: '今天', icon: Sun, count: 4 },
  { id: 'upcoming', name: '即将到来', icon: CalendarDays, count: 12 },
  { id: 'calendar', name: '日历', icon: Calendar },
  { id: 'notes', name: '便签板', icon: StickyNote },
];

const lists = [
  { id: 'personal', name: '个人', icon: User, color: 'bg-purple-500', count: 3 },
  { id: 'work', name: '工作', icon: Briefcase, color: 'bg-blue-500', count: 6 },
  { id: 'shopping', name: '购物清单', icon: ShoppingCart, color: 'bg-green-500', count: 2 },
];

export function Sidebar({ activeNav, onNavChange }: SidebarProps) {
  return (
    <aside className="w-64 h-screen bg-sidebar flex flex-col border-r border-sidebar-border">
      {/* User Profile */}
      <div className="p-4 flex items-center gap-3">
        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-purple-500 to-blue-500 flex items-center justify-center text-sm font-medium text-primary-foreground">
          EW
        </div>
        <div className="flex-1">
          <h3 className="font-medium text-sidebar-foreground">Eric Wu</h3>
        </div>
        <ChevronDown className="w-4 h-4 text-muted-foreground" />
      </div>

      {/* Search */}
      <div className="px-4 mb-4">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <input
            type="text"
            placeholder="搜索"
            className="w-full bg-sidebar-accent text-sidebar-foreground pl-10 pr-4 py-2 rounded-lg text-sm placeholder:text-muted-foreground focus:outline-none focus:ring-1 focus:ring-sidebar-ring"
          />
        </div>
      </div>

      {/* Navigation */}
      <nav className="px-2 space-y-1">
        {navItems.map((item) => (
          <button
            key={item.id}
            onClick={() => onNavChange(item.id)}
            className={cn(
              'w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition-colors',
              activeNav === item.id
                ? 'bg-sidebar-accent text-sidebar-foreground'
                : 'text-muted-foreground hover:bg-sidebar-accent/50 hover:text-sidebar-foreground'
            )}
          >
            <item.icon className="w-5 h-5" />
            <span className="flex-1 text-left">{item.name}</span>
            {item.count && (
              <span className="text-muted-foreground">{item.count}</span>
            )}
          </button>
        ))}
      </nav>

      {/* Lists Section */}
      <div className="mt-6 px-4">
        <div className="flex items-center justify-between mb-2">
          <h4 className="text-xs font-medium text-muted-foreground uppercase tracking-wider">列表</h4>
          <ChevronDown className="w-4 h-4 text-muted-foreground" />
        </div>
        <div className="space-y-1">
          {lists.map((list) => (
            <button
              key={list.id}
              onClick={() => onNavChange(list.id)}
              className={cn(
                'w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition-colors',
                activeNav === list.id
                  ? 'bg-sidebar-accent text-sidebar-foreground'
                  : 'text-muted-foreground hover:bg-sidebar-accent/50 hover:text-sidebar-foreground'
              )}
            >
              <div className={cn('w-2 h-2 rounded-full', list.color)} />
              <span className="flex-1 text-left">{list.name}</span>
              <span className="text-muted-foreground">{list.count}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Bottom Actions */}
      <div className="mt-auto p-4 space-y-2">
        <button className="w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm text-muted-foreground hover:bg-sidebar-accent/50 hover:text-sidebar-foreground transition-colors">
          <Plus className="w-5 h-5" />
          <span>添加新列表</span>
        </button>
        <button className="w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm text-muted-foreground hover:bg-sidebar-accent/50 hover:text-sidebar-foreground transition-colors">
          <Tag className="w-5 h-5" />
          <span>标签</span>
        </button>
      </div>
    </aside>
  );
}

import { useState } from 'react';
import { Plus, ChevronDown, ChevronRight, Clock, Flag } from 'lucide-react';
import { cn } from '@/lib/utils';
import type { Task } from '@/types/task';

interface TaskListProps {
  tasks: Task[];
  selectedTaskId: string | null;
  onSelectTask: (task: Task) => void;
  onToggleTask: (taskId: string) => void;
  onToggleSubtask: (taskId: string, subtaskId: string) => void;
}

const priorityColors = {
  high: 'text-red-500',
  medium: 'text-yellow-500',
  low: 'text-green-500',
};

const priorityLabels = {
  high: '高',
  medium: '中',
  low: '低',
};

const categoryColors: Record<string, string> = {
  '个人': 'bg-purple-500/20 text-purple-400',
  '工作': 'bg-blue-500/20 text-blue-400',
  '购物清单': 'bg-green-500/20 text-green-400',
};

export function TaskList({ tasks, selectedTaskId, onSelectTask, onToggleTask, onToggleSubtask }: TaskListProps) {
  const [expandedTasks, setExpandedTasks] = useState<Set<string>>(new Set(['1']));

  const toggleExpand = (taskId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    setExpandedTasks(prev => {
      const newSet = new Set(prev);
      if (newSet.has(taskId)) {
        newSet.delete(taskId);
      } else {
        newSet.add(taskId);
      }
      return newSet;
    });
  };

  const today = new Date();
  const dateString = today.toLocaleDateString('zh-CN', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long',
  });

  return (
    <main className="flex-1 h-screen overflow-hidden flex flex-col bg-background">
      {/* Header */}
      <header className="p-6 pb-4">
        <h1 className="text-2xl font-bold text-foreground">今天</h1>
        <p className="text-muted-foreground text-sm mt-1">{dateString}</p>
      </header>

      {/* Task List */}
      <div className="flex-1 overflow-y-auto px-6 pb-6 scrollbar-thin">
        <div className="space-y-3">
          {tasks.map((task) => (
            <div key={task.id}>
              <div
                onClick={() => onSelectTask(task)}
                className={cn(
                  'group p-4 rounded-xl cursor-pointer transition-all',
                  'bg-[hsl(var(--task-card))] hover:bg-[hsl(var(--task-card-hover))]',
                  selectedTaskId === task.id && 'ring-1 ring-primary'
                )}
              >
                <div className="flex items-start gap-3">
                  {/* Expand Button */}
                  {task.subtasks.length > 0 && (
                    <button
                      onClick={(e) => toggleExpand(task.id, e)}
                      className="mt-0.5 text-muted-foreground hover:text-foreground transition-colors"
                    >
                      {expandedTasks.has(task.id) ? (
                        <ChevronDown className="w-4 h-4" />
                      ) : (
                        <ChevronRight className="w-4 h-4" />
                      )}
                    </button>
                  )}

                  {/* Checkbox */}
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      onToggleTask(task.id);
                    }}
                    className={cn(
                      'w-5 h-5 rounded-full border-2 flex items-center justify-center transition-colors flex-shrink-0 mt-0.5',
                      task.completed
                        ? 'bg-primary border-primary'
                        : 'border-muted-foreground hover:border-primary'
                    )}
                  >
                    {task.completed && (
                      <svg className="w-3 h-3 text-primary-foreground" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
                      </svg>
                    )}
                  </button>

                  {/* Content */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2">
                      <span className={cn(
                        'font-medium text-foreground',
                        task.completed && 'line-through text-muted-foreground'
                      )}>
                        {task.title}
                      </span>
                    </div>

                    <div className="flex items-center gap-3 mt-2">
                      {task.time && (
                        <div className="flex items-center gap-1 text-xs text-muted-foreground">
                          <Clock className="w-3 h-3" />
                          <span>{task.time}</span>
                        </div>
                      )}
                      <div className={cn('px-2 py-0.5 rounded text-xs', categoryColors[task.category] || 'bg-muted text-muted-foreground')}>
                        {task.category}
                      </div>
                      <div className={cn('flex items-center gap-1 text-xs', priorityColors[task.priority])}>
                        <Flag className="w-3 h-3" />
                        <span>{priorityLabels[task.priority]}</span>
                      </div>
                      {task.subtasks.length > 0 && (
                        <span className="text-xs text-muted-foreground">
                          {task.subtasks.filter(s => s.completed).length}/{task.subtasks.length} 子任务
                        </span>
                      )}
                    </div>
                  </div>
                </div>

                {/* Subtasks */}
                {expandedTasks.has(task.id) && task.subtasks.length > 0 && (
                  <div className="mt-3 ml-12 space-y-2">
                    {task.subtasks.map((subtask) => (
                      <div key={subtask.id} className="flex items-center gap-3">
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            onToggleSubtask(task.id, subtask.id);
                          }}
                          className={cn(
                            'w-4 h-4 rounded border flex items-center justify-center transition-colors flex-shrink-0',
                            subtask.completed
                              ? 'bg-primary border-primary'
                              : 'border-muted-foreground hover:border-primary'
                          )}
                        >
                          {subtask.completed && (
                            <svg className="w-2.5 h-2.5 text-primary-foreground" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
                            </svg>
                          )}
                        </button>
                        <span className={cn(
                          'text-sm',
                          subtask.completed ? 'text-muted-foreground line-through' : 'text-foreground'
                        )}>
                          {subtask.title}
                        </span>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Add Task Button */}
        <button className="mt-4 w-full p-4 rounded-xl border-2 border-dashed border-muted hover:border-primary/50 text-muted-foreground hover:text-foreground transition-colors flex items-center justify-center gap-2">
          <Plus className="w-5 h-5" />
          <span>添加新任务</span>
        </button>
      </div>
    </main>
  );
}

import { X, Calendar, Clock, Flag, Paperclip, Trash2, Plus } from 'lucide-react';
import { cn } from '@/lib/utils';
import type { Task } from '@/types/task';

interface TaskDetailProps {
  task: Task | null;
  onClose: () => void;
  onToggleSubtask: (taskId: string, subtaskId: string) => void;
}

const priorityColors = {
  high: 'text-red-500',
  medium: 'text-yellow-500',
  low: 'text-green-500',
};

const priorityLabels = {
  high: '高优先级',
  medium: '中优先级',
  low: '低优先级',
};

export function TaskDetail({ task, onClose, onToggleSubtask }: TaskDetailProps) {
  if (!task) return null;

  return (
    <aside className="w-80 h-screen bg-[hsl(var(--detail-panel))] border-l border-border flex flex-col">
      {/* Header */}
      <div className="p-4 flex items-center justify-between border-b border-border">
        <h2 className="font-medium text-foreground">任务详情</h2>
        <button
          onClick={onClose}
          className="p-1 rounded hover:bg-muted text-muted-foreground hover:text-foreground transition-colors"
        >
          <X className="w-5 h-5" />
        </button>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4 space-y-6 scrollbar-thin">
        {/* Title */}
        <div>
          <h3 className="text-lg font-medium text-foreground">{task.title}</h3>
        </div>

        {/* Date & Time */}
        <div className="space-y-3">
          <div className="flex items-center gap-3 text-sm">
            <Calendar className="w-4 h-4 text-muted-foreground" />
            <span className="text-foreground">{task.date}</span>
          </div>
          {task.time && (
            <div className="flex items-center gap-3 text-sm">
              <Clock className="w-4 h-4 text-muted-foreground" />
              <span className="text-foreground">{task.time}</span>
            </div>
          )}
          <div className="flex items-center gap-3 text-sm">
            <Flag className={cn('w-4 h-4', priorityColors[task.priority])} />
            <span className={priorityColors[task.priority]}>{priorityLabels[task.priority]}</span>
          </div>
        </div>

        {/* Notes */}
        <div>
          <h4 className="text-sm font-medium text-muted-foreground mb-2">备注</h4>
          <textarea
            placeholder="添加备注..."
            defaultValue={task.notes}
            className="w-full bg-muted/50 text-foreground rounded-lg p-3 text-sm placeholder:text-muted-foreground focus:outline-none focus:ring-1 focus:ring-primary resize-none min-h-[80px]"
          />
        </div>

        {/* Subtasks */}
        <div>
          <h4 className="text-sm font-medium text-muted-foreground mb-3">子任务</h4>
          <div className="space-y-2">
            {task.subtasks.map((subtask) => (
              <div key={subtask.id} className="flex items-center gap-3 p-2 rounded-lg hover:bg-muted/30 transition-colors">
                <button
                  onClick={() => onToggleSubtask(task.id, subtask.id)}
                  className={cn(
                    'w-4 h-4 rounded border flex items-center justify-center transition-colors flex-shrink-0',
                    subtask.completed
                      ? 'bg-primary border-primary'
                      : 'border-muted-foreground hover:border-primary'
                  )}
                >
                  {subtask.completed && (
                    <svg className="w-2.5 h-2.5 text-primary-foreground" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
                    </svg>
                  )}
                </button>
                <span className={cn(
                  'text-sm flex-1',
                  subtask.completed ? 'text-muted-foreground line-through' : 'text-foreground'
                )}>
                  {subtask.title}
                </span>
              </div>
            ))}
            <button className="w-full flex items-center gap-2 p-2 text-sm text-muted-foreground hover:text-foreground transition-colors">
              <Plus className="w-4 h-4" />
              <span>添加子任务</span>
            </button>
          </div>
        </div>
      </div>

      {/* Footer Actions */}
      <div className="p-4 border-t border-border flex items-center justify-between">
        <button className="flex items-center gap-2 text-sm text-muted-foreground hover:text-foreground transition-colors">
          <Paperclip className="w-4 h-4" />
          <span>附件</span>
        </button>
        <button className="flex items-center gap-2 text-sm text-destructive hover:text-destructive/80 transition-colors">
          <Trash2 className="w-4 h-4" />
          <span>删除</span>
        </button>
      </div>
    </aside>
  );
}

